mapper.select:
select * from (select buyNum, subname, subaddr1, subaddr2, subphonenum, buyamount, price, aname, asize, buydate, buyStatus , sendContent,sendNum,sendpackage, rownum r 
from (select * from buy order by buyNum desc ) ) 
where r between #{startRow} and #{endRow}

select * from (select buyNum, subname, subaddr1, subaddr2, subphonenum, buyamount, price, aname, asize, buydate, buyStatus, sendContent, sendNum, sendpackage, companyNum, rownum r 
from (select * from buy where companyNum = #{companyNum} order by buyNum desc ) ) 
where r between #{startRow} and #{endRow}
